#include <iostream>#include <cstdio>#include <cmath>#define EPS 1.0E-9int equals(double a, double b) {	return fabs(a - b) < EPS;}int greaterThan(double a, double b) {	return (a - b) > EPS;}using namespace std;int main() {	double a, b, c, i, j;	while (scanf("%lf %lf %lf", &a, &b, &c) == 3) {		if (greaterThan(b, a)) {			i = b;			b = a;			a = i;		}		if (greaterThan(c, a)) {			i = c;			c = a;			a = i;		}		i = b + c;		if (greaterThan(a, i) || equals(a, i)) {			printf("NAO FORMA TRIANGULO\n");		} else {			i = a * a;			j = (b * b) + (c * c);			if (equals(i, j)) {				printf("TRIANGULO RETANGULO\n");			} else if (greaterThan(i, j)) {				printf("TRIANGULO OBTUSANGULO\n");			} else {				printf("TRIANGULO ACUTANGULO\n");			}			if (equals(a, b) && equals(b, c) && equals(a, c)) {				printf("TRIANGULO EQUILATERO\n");			} else if (equals(a, b) || equals(b, c) || equals(a, c)) {				printf("TRIANGULO ISOSCELES\n");			}		}	}	return 0;}
