#include <iostream>#include <string>#include <vector>#include <algorithm>using namespace std;const vector<string> cities = {"Brasilia", "Salvador", "Sao Paulo", "Rio de Janeiro", "Juiz de Fora", "Campinas", "Vitoria", "Belo Horizonte"};const vector<int> ddds = {61, 71, 11, 21, 32, 19, 27, 31};int main() {	int ddd;	while (scanf("%d", &ddd) == 1) {		auto i = find(ddds.begin(), ddds.end(), ddd);		if (i != ddds.end()) {			cout << cities[i - ddds.begin()] << "\n";		} else {			cout << "DDD nao cadastrado\n";		}	}	return 0;}
