#include <iostream>using namespace std;#define EPS 1.0E-3#define RANGES 4const double salB[RANGES] = {2000, 3000, 4500, -1};const double per[RANGES] = {0, 0.08, 0.18, 0.28};int lessT(double a, double b) {	return (a - b) < EPS;}double getSalRange(double sal) {	int i, si = RANGES - 1;	for (i = 0; i < RANGES; i++) {		if (lessT(sal, salB[i])) {			si = i;			break;		}	}	return si;}double zeroOrMore(double a) {	return lessT(a, 0) ? 0 : a;}int main() {	double sal, r, rs, rss, rsss, total;	int si;	while (scanf("%lf", &sal) == 1) {		si = getSalRange(sal);		r = per[si];		if (si == 0) {			printf("Isento\n");		} else {			rsss = zeroOrMore(sal - 4500.0);			rss = zeroOrMore(sal - 3000.0) - rsss;			rs = zeroOrMore(sal - 2000.0) - rss - rsss;			printf("R$ %.2lf\n", rs * 0.08 + rss * 0.18 + rsss * 0.28);		}	}	return 0;}
