#include <bits/stdc++.h>using namespace std;const std::vector<string> months = {"January",									"February",									"March",									"April",									"May",									"June",									"July",									"August",									"September",									"October",									"November",									"December"};int main() {	int m;	while (scanf("%d", &m) == 1) {		cout << months[m - 1] << "\n";	}	return 0;}
