#include <iostream>#include <cmath>using namespace std;int main() {	int t, x, r;	while (cin >> t) {		while (t-- && cin >> x) {			r = abs(x);			if (x) {				if (r%2) {					printf("ODD ");				} else {					printf("EVEN ");				}				if (x > 0) {					printf("POSITIVE\n");				} else {					printf("NEGATIVE\n");				}			} else {				printf("NULL\n");			}		}	}	return 0;}
