#include <iostream>#include <cstring>using namespace std;int main() {	int n, x;	char t;	int ani[3], a, i;	while(cin >> n) {		memset(ani, 0, 3 * sizeof(int));		while (n-- && scanf("%d %c", &x, &t) == 2) {			switch(t) {				case 'C': i = 0; break;				case 'R': i = 1; break;				case 'S': i = 2; break;			}			ani[i] += x;		}		a = ani[0] + ani[1] + ani[2];		printf("Total: %d cobaias\n", a);		printf("Total de coelhos: %d\n", ani[0]);		printf("Total de ratos: %d\n", ani[1]);		printf("Total de sapos: %d\n", ani[2]);		printf("Percentual de coelhos: %.2lf %%\n", (ani[0] * 100.0)/a);		printf("Percentual de ratos: %.2lf %%\n", (ani[1] * 100.0)/a);		printf("Percentual de sapos: %.2lf %%\n", (ani[2] * 100.0)/a);	}	return 0;}
