#include <iostream>using namespace std;int main() {	int x, y, i, g, e, a;	i = g = e = 0;	do {		a = scanf("%d %d", &x, &y);		if (x > y)			i++;		else if (x < y)			g++;		else			e++;		printf("Novo grenal (1-sim 2-nao)\n");	} while (scanf("%d", &a), a == 1);	printf("%d grenais\n", i + g + e);	printf("Inter:%d\n", i);	printf("Gremio:%d\n", g);	printf("Empates:%d\n", e);	if (i != g)		printf("%s venceu mais\n", i > g ? "Inter" : "Gremio");	else		printf("Nao houve vencedor\n");	return 0;}