#include <iostream>#include <cmath>using namespace std;int main() {	int t, i;	long pa, pb;	double g1, g2;	while (cin >> t) {		while(t--) {			cin >> pa >> pb >> g1 >> g2;			g1 /= 100.0;			g2 /= 100.0;			for (i = 1; pa <= pb; i++) {				pa += (long) floor((double) pa * g1);				pb += (long) floor((double) pb * g2);				if (i > 101) {					break;				}			}			i--;			if (i > 100)				printf("Mais de 1 seculo.\n");			else				printf("%d anos.\n", i);		}	}	return 0;}