#include <iostream>#include <cmath>#define EPS 1.0E-9using namespace std;bool lessT(double a, double b) {	return (a - b) < EPS;}bool eq(double a, double b) {	return fabs(a-b) < EPS;}int main() {	double x[100];	int v, n, i;	n = 0;	while (n < 100 && cin >> (x[n]))		n++;	for (i = 0; i < 100; i++)		if (eq(x[i], 10.0) || lessT(x[i], 10.0))			printf("A[%d] = %.1lf\n", i, x[i]);	return 0;}