#include <iostream>using namespace std;int main() {	int n, i, j, a, m;	while (cin >> n && n) {		m = n/2;		for (i = 0; i < n; i++) {			for (j = 0; j < n; j++) {				if (j)					printf(" ");				a = abs(i - j) + 1;				printf("%3d", a);			}			printf("\n");		}		printf("\n");	}	return 0;}