#include <iostream>using namespace std;int main() {	int n, i, t, v;	long long total;	int s[1000000];	char vis[1000000];	scanf("%d", &n);	total = 0;	for (i = 0; i < n; i++) {		scanf("%d", s+i);		total += s[i];		vis[i] = 0;	}	i = 0;	v = 0;	while (i >= 0 && i < n) {		if (!vis[i]) {			v += (vis[i] = 1);		}		if (s[i]) {			total--;			if (s[i]%2)				s[i++]--;			else				s[i--]--;		} else {			i--;		}	}	printf("%d %lld\n", v, total);	return 0;}
