#include <iostream>using namespace std;int main() {	int r, s, l, n, t, i, is;	while (cin >> t) {		for (i = s = 0, is = 1; i < t; i++) {			cin >> n;			if (is) {				switch(i) {					case 0:						break;					case 1:						if (n < l) {							s = 1;						} else if (n > l) {							s = -1;						} else {							is = 0;						}						break;					default:						if (n < l && s < 0) {							s = 1;						} else if (n > l && s > 0) {							s = -1;						} else {							is = 0;						}						break;				}				l = n;			}		}		printf("%d\n", is);	}	return 0;}
