#include <iostream>using namespace std;int nums[1100][1100];int m, n;int checkIf(int x, int y) {	int i, j, is, r, d;	is = 1;	r = x + 2;	d = y + 2;	if (r >= m || d >= n) {		return 0;	}	for (i = x; i <= r && i < m; i++) {		for (j = y; j <= d && j < n; j++) {			if (i == x || j == y || i == r || j == d) {				if (nums[i][j] != 7) {					return 0;				}			} else {				if (nums[i][j] != 42) {					return 0;				}			}		}	}	return is;}int main() {	int i, j, f, a, b, z;	z = scanf("%d %d", &m, &n);	for (i = 0; i < m; i++) {		for (j = 0; j < n; j++) {			z = scanf("%d", nums[i]+j);		}	}	a = b = 0;	f = 0;	for (i = 0; i < m && !f; i++) {		for (j = 0; j < n; j++) {			if (checkIf(i, j)) {				f = 1;				a = i + 2;				b = j + 2;					break;			}		}	}	printf("%d %d\n", a, b);	return 0;}
