#include <iostream>using namespace std;#define EPS 1.0E-3int lessT(double a, double b) {	return (a - b) < EPS;}int main() {	int ranges = 5;	double salB[5] = {400, 800, 1200, 2000, -1};	double per[5] = {0.15, 0.12, 0.1, 0.07, 0.04};	double sal, r;	int si, i;	while(scanf("%lf", &sal) == 1) {		si = 4;		for (i = 0; i < 5; i++) {			if (lessT(sal, salB[i])) {				si = i;				break;			}		}		r = per[si] * sal;		printf("Novo salario: %.2lf\n", sal + r);		printf("Reajuste ganho: %.2lf\n", r);		printf("Em percentual: %.0lf %%\n", per[si] * 100);	}	return 0;}
