#include <iostream>using namespace std;int main() {	int sD, sH, sM, sS, fD, fH, fM, fS;	long long s, f, d;	long long w, x, y, z;	if (scanf("Dia %d\n%d : %d : %d\n", &sD, &sH, &sM, &sS) == 4 &&		scanf("Dia %d\n%d : %d : %d", &fD, &fH, &fM, &fS) == 4) {		s = sS + (sM * 60) + (sH * 3600) + (sD * 86400);		f = fS + (fM * 60) + (fH * 3600) + (fD * 86400);		d = f - s;		w = d/86400;		d %= 86400;		x = d/3600;		d %= 3600;		y = d/60;		z = d%60;		// printf("%lld\n", d);		printf("%lld dia(s)\n%lld hora(s)\n%lld minuto(s)\n%lld segundo(s)\n", w, x, y, z);	}	return 0;}
