#include <iostream>using namespace std;#define N 5int main() {	int e, o, p, ne, i;	e = o = p = ne = 0;	int n;	for (i = 0; i < N; i++) {		if (scanf("%d", &n) == 1) {			if (n%2) {				o++;			} else {				e++;			}			if (n < 0) {				ne++;			} else if (n > 0) {				p++;			}		}	}	printf("%d valor(es) par(es)\n", e);	printf("%d valor(es) impar(es)\n", o);	printf("%d valor(es) positivo(s)\n", p);	printf("%d valor(es) negativo(s)\n", ne);	return 0;}