#include <iostream>using namespace std;int main() {	int n, i, j, d, a, h, t, ul;	while (cin >> n) {		h = n/2;		t = n/3;		ul = n - t;		for (i = 0; i < n; i++) {			for (j = 0; j < n; j++) {				if (i == j && i == h) {					d = 4;				} else if (i >= t && i < ul && j >= t && j < ul) {					d = 1;				} else if (i == j) {					d = 2;				} else if (i + j == n - 1) {					d = 3;				} else {					d = 0;				}				printf("%d", d);			}			printf("\n");		}		printf("\n");	}	return 0;}
