#include <iostream>#include <cmath>using namespace std;#define EPS 1.0E-9bool lessT(double a, double b) {	return (a - b) < EPS;}bool greaterT(double a, double b) {	return lessT(b, a);}bool equalsT(double a, double b) {	return fabs(a - b) < EPS;}int main() {	int t, i, a;	double b, x;	while (cin >> t) {		i = 0;		while (t--) {			scanf("%d %lf", &a, &x);			if (i) {				if (lessT(b, x)) {					b = x;					i = a;				}			} else if (greaterT(x, 8.0) || equalsT(x, 8.0)) {				b = x;				i = a;			}		}		if (i) {			printf("%d\n", i);		} else {			printf("Minimum note not reached\n");		}	}	return 0;}
