#include <iostream>using namespace std;int main() {	int n, c, i, j, t;	t = 0;	while (cin >> n) {		t++;		for (c = 0, i = 1; i <= n; i++) {			c += i;		}		printf("Caso %d: %d numero%s\n", t, c + 1, c == 1 ? "" : "s");		for (i = 0; i <= n; i++) {			if (i)				printf(" ");			for (j = 0; j < (i ? i : 1); j++) {				printf("%s%d", j ? " " : "", i);			}		}		printf("\n\n");	}	return 0;}
